mapper.insert:
INSERT INTO file(
	file_code
	, file_name
	, file_sn
	, file_mime
	, file_size
	, file_fancysize
	, file_rdate
	, file_path
	, show_profileimg
)
VALUES(
	#{fileCode}
	, #{fileName}
	, #{fileSn}
	, #{fileMime}
	, #{fileSize}
	, #{fileFancysize}
	, #{fileRdate}
	, #{filePath}
	, COALESCE(#{showProfileimg},"N")

)
